// ========================================
// MERGED DYNAMIC HTML PARSER
// Combines: Your hash & formatting + My flexibility
// ========================================

let results = [];
let processedKeys = new Set();

// ========================================
// HASH GENERATION (Your Code)
// ========================================
function generateHash(string) {
  let hash = 0;
  for (let i = 0; i < string.length; i++) {
    const char = string.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash |= 0;
  }
  return Math.abs(hash).toString();
}

// ========================================
// SMART DATE EXTRACTION (Merged)
// ========================================
function extractDate(text) {
  // Your powerful regex pattern
  const dateRegex = /(\d{1,2})?\s*(?:st|nd|rd|th)?\s*([a-z]{3,9})\s*(?:st|nd|rd|th)?\s*,?\s*(\d{1,2})?\s*(?:st|nd|rd|th)?\s*,?\s*(202\d)/i;
  const dateMatch = text.match(dateRegex);

  if (dateMatch) {
    try {
      const monthStr = dateMatch[2];
      const yearStr = dateMatch[4];
      const dayStr = dateMatch[1] || dateMatch[3] || "01";
      const parsedDate = new Date(`${monthStr} ${dayStr}, ${yearStr}`);

      if (!isNaN(parsedDate.getTime())) {
        parsedDate.setHours(23, 59, 59);
        const timestamp = Math.floor(parsedDate.getTime() / 1000);
        console.log(`ðŸ“… Found closing date: ${parsedDate.toDateString()}`);
        return timestamp;
      }
    } catch (e) {
      console.log(`âš ï¸ Date parsing error: ${e.message}`);
    }
  }

  // My fallback: Dynamic 30 days from now
  console.log(`âš ï¸ No valid date found, using 30-day default`);
  const defaultDate = new Date();
  defaultDate.setDate(defaultDate.getDate() + 30);
  defaultDate.setHours(23, 59, 59);
  return Math.floor(defaultDate.getTime() / 1000);
}

// ========================================
// DYNAMIC COLUMN DETECTION (My Code)
// ========================================
function findColumns(headerCells) {
  let serialCol = -1;
  let designationCol = -1;
  let eligibilityCol = -1;

  headerCells.forEach((cell, index) => {
    const lower = cell.toLowerCase().trim();
    if (lower.includes('s#') || lower.includes('serial') || lower === 'no') {
      serialCol = index;
    }
    if (lower.includes('designation') || lower.includes('post')) {
      designationCol = index;
    }
    if (lower.includes('eligibility') || lower.includes('qualification')) {
      eligibilityCol = index;
    }
  });

  // Defaults if not found
  if (designationCol === -1) designationCol = 1;
  if (eligibilityCol === -1) eligibilityCol = 2;

  return { serialCol, designationCol, eligibilityCol };
}

// ========================================
// EXTRACT CELLS FROM TABLE ROW (My Code)
// ========================================
function extractCells(rowHtml) {
  const cellRegex = /<t[dh][^>]*>([\s\S]*?)<\/t[dh]>/gi;
  const cells = [...rowHtml.matchAll(cellRegex)];

  return cells.map(cell => {
    let text = cell[1]
      .replace(/<br\s*\/?>/gi, '\n')
      .replace(/<\/p>/gi, '\n')
      .replace(/<li[^>]*>/gi, '\nâ€¢ ')
      .replace(/<\/li>/gi, '')
      .replace(/<ul[^>]*>/gi, '')
      .replace(/<\/ul>/gi, '')
      .replace(/<[^>]*>/g, ' ')
      .replace(/&nbsp;/g, ' ')
      .replace(/&amp;/g, '&')
      .replace(/&lt;/g, '<')
      .replace(/&gt;/g, '>')
      .replace(/&quot;/g, '"')
      .replace(/&#39;/g, "'")
      .replace(/\r/g, '')
      .replace(/\t+/g, ' ')
      .replace(/ +/g, ' ');

    return text.trim();
  });
}

// ========================================
// ENHANCED CONTENT FORMATTING (Your Code + My Keywords)
// ========================================
function formatContent(text, isEligibility = false) {
  if (!text || text === "N/A" || text.length < 10) return "N/A";

  const footerKeywords = /(AHL is an equal opportunity|CAREER OPPORTUNITY|HR Department|Northwest General Hospital)/i;
  const splitIndex = text.search(footerKeywords);

  let mainRules = text;
  let footerText = "";

  if (splitIndex !== -1) {
    mainRules = text.substring(0, splitIndex).trim();
    footerText = text.substring(splitIndex).trim();
  }

  // Better line splitting - preserve complete sentences
  let rawLines;

  if (isEligibility) {
    // For eligibility: split more carefully
    rawLines = mainRules
      .split(/\n/)
      .map(line => line.trim())
      .filter(line => line.length > 0);

    // Merge incomplete lines
    const mergedLines = [];
    let currentLine = "";

    for (let line of rawLines) {
      // Remove bullet symbols
      line = line.replace(/^[â€¢â—â—‹â– â—†â–ªâ–«â¦¿â¦¾\-]+\s*/g, '').trim();

      // If line seems incomplete (no ending punctuation and short), merge with next
      if (currentLine && line.length > 0) {
        currentLine += " " + line;
      } else if (line.length > 0) {
        currentLine = line;
      }

      // If we have a complete sentence or a long enough line, save it
      if (currentLine.match(/[.!?]$/) || currentLine.length > 80) {
        mergedLines.push(currentLine);
        currentLine = "";
      }
    }

    // Don't forget the last line
    if (currentLine.length > 0) {
      mergedLines.push(currentLine);
    }

    rawLines = mergedLines;
  } else {
    // For terms: split by periods or bullets
    rawLines = mainRules.split(/[â€¢.\n](?=\s|[A-Z])/).filter(l => l.trim().length > 5);
  }

  // Enhanced keyword filtering for eligibility
  const eligibilityKeywords = /candidate|experience|qualification|degree|master|bachelor|university|HEC|skills|proficient|relevant|preferred|years|hospital|minimum|required|strong|proven|demonstrated|valid|license|BBA|MBA|MBBS|MD|Pharm|diploma|BS|MS|MPhil|PhD|JCI|ISO|inventory|management|forecasting|procurement|stock|automation|efficiency|cost|control|certified|registration|council|ability|knowledge|understanding|familiar|expertise|competent|fluent|excellent|good|working|training|certification|accredited|recognized|graduate|post-graduate|intermediate|matric|HSSC|SSC|technical|professional|age limit|nationality|domicile|command|liaison|coordination|verbal|written|office|communication/i;

  let filteredLines = rawLines;

  // Apply keyword filter only for eligibility
  if (isEligibility) {
    filteredLines = rawLines.filter(line => {
      const cleanLine = line.trim();
      return cleanLine.length > 15 && eligibilityKeywords.test(cleanLine);
    });
  }

  // Your numbering format - ensure proper punctuation
  let formattedMain = filteredLines
    .map((line, index) => {
      let cleanLine = line.trim();
      // Add period if missing
      if (!cleanLine.match(/[.!?]$/)) {
        cleanLine += '.';
      }
      return `${index + 1}. ${cleanLine}`;
    })
    .join('<br>');

  // Your footer formatting (Bold + Line breaks)
  if (footerText) {
    let formattedFooter = footerText
      .replace(/AHL is an equal opportunity/g, 'AHL is an equal opportunity')
      .replace(/CAREER OPPORTUNITY/g, '<br><br>CAREER OPPORTUNITY')
      .replace(/HR Department/g, '<br>HR Department')
      .replace(/Northwest General Hospital/g, '<br>Northwest General Hospital')
      .replace(/Sector A3/g, '<br>Sector A3')
      .replace(/Ph:/g, '<br>Ph:');

    return `${formattedMain}<br><br><strong>${formattedFooter}</strong>`;
  }

  return formattedMain;
}

// ========================================
// HELPER FUNCTIONS
// ========================================
function isHeader(text) {
  const lower = text.toLowerCase().trim();
  return /^(s#|serial|sr|no|designation|eligibility|qualification|position|post)$/i.test(lower) ||
         text.length < 3 ||
         /^\d+$/.test(text);
}

// ========================================
// MAIN PROCESSING
// ========================================
const items = $input.all();

console.log(`ðŸ“¥ Processing ${items.length} items`);

for (const item of items) {
  const html = item.json.data || item.json.body || "";
  const sender = item.json.sender || "Unknown";
  const fileName = item.json.originalFileName || "";

  if (!html || html.length < 100) {
    console.log(`â­ï¸  Skipping: No HTML content`);
    continue;
  }

  console.log(`\nðŸ“„ File: ${fileName}`);
  console.log(`ðŸ“Š HTML: ${html.length} chars`);

  // ========================================
  // 1. EXTRACT TERMS & CONDITIONS
  // ========================================
  let termsSection = "N/A";
  const termsRegex = /TERMS[\s\S]*?CONDITIONS[:\s]*([\s\S]*?)(?:<\/div>|<\/body>|$)/i;
  const termsMatch = html.match(termsRegex);

  if (termsMatch && termsMatch[1]) {
    termsSection = termsMatch[1]
      .replace(/<p[^>]*>/gi, '\n')
      .replace(/<li[^>]*>/gi, '\n')
      .replace(/<br\s*\/?>/gi, '\n')
      .replace(/<[^>]*>/g, ' ')
      .replace(/&nbsp;/g, ' ')
      .replace(/&amp;/g, '&')
      .replace(/\s+/g, ' ')
      .trim();

    console.log(`ðŸ“œ Terms found: ${termsSection.length} chars`);
  }

  // ========================================
  // 2. EXTRACT CLOSING DATE
  // ========================================
  const textBody = html
    .replace(/<br\s*\/?>/gi, '\n')
    .replace(/<\/p>/gi, '\n')
    .replace(/<[^>]*>/g, ' ')
    .replace(/&nbsp;/g, ' ')
    .replace(/\s+/g, ' ');

  const closingTimestamp = extractDate(termsSection + " " + textBody);

  // ========================================
  // 3. PARSE TABLE WITH DYNAMIC COLUMNS
  // ========================================
  const tableRegex = /<table[^>]*>([\s\S]*?)<\/table>/gi;
  const tables = [...html.matchAll(tableRegex)];

  console.log(`ðŸ“Š Found ${tables.length} tables`);

  for (let tableIndex = 0; tableIndex < tables.length; tableIndex++) {
    const tableHtml = tables[tableIndex][1];
    const rowRegex = /<tr[^>]*>([\s\S]*?)<\/tr>/gi;
    const rows = [...tableHtml.matchAll(rowRegex)];

    console.log(`  Table ${tableIndex + 1}: ${rows.length} rows`);

    if (rows.length < 2) continue;

    // Parse header to find columns dynamically
    const headerCells = extractCells(rows[0][1]);
    const columns = findColumns(headerCells);

    console.log(`  Columns: Designation=${columns.designationCol}, Eligibility=${columns.eligibilityCol}`);

    // Process data rows
    for (let i = 1; i < rows.length; i++) {
      const cells = extractCells(rows[i][1]);

      if (cells.length < 2) continue;

      const jobTitle = (cells[columns.designationCol] || cells[1] || "")
        .trim()
        .replace(/\s+/g, ' ');

      const rawEligibility = (cells[columns.eligibilityCol] || cells[2] || cells[cells.length - 1] || "").trim();

      // Validate
      if (jobTitle.length < 3 || isHeader(jobTitle)) {
        console.log(`  Row ${i}: Skipped (header or invalid)`);
        continue;
      }

      // Check for duplicates using hash
      const externalId = generateHash(jobTitle + fileName);
      const dupKey = jobTitle + sender + fileName;

      if (processedKeys.has(dupKey)) {
        console.log(`  â­ï¸  Skip duplicate: ${jobTitle}`);
        continue;
      }
      processedKeys.add(dupKey);

      // Format eligibility and terms
      const bulletEligibility = formatContent(rawEligibility, true);
      const bulletTerms = formatContent(termsSection, false);

      if (bulletEligibility === "N/A" || bulletEligibility.length < 50) {
        console.log(`  âš ï¸  Skipping ${jobTitle}: Insufficient eligibility data`);
        continue;
      }

      console.log(`  âœ… Adding: ${jobTitle}`);

      results.push({
        json: {
          isValid: true,
          title: jobTitle,
          slug: `${jobTitle.toLowerCase().replace(/[^a-z0-9]+/g, '-')}-${externalId}`,
          external_id: externalId,
          documentName: jobTitle,
          content: `Northwest General Hospital requires the services of: ${jobTitle}`,
          status: "publish",
          "application-email": "recruitment.hr@nwgh.pk",
          "date-close": closingTimestamp,
          location: "Northwest General Hospital & Research Centre, Peshawar",
          "type-full-time-part-time-contract": "Full Time",
          "job-qualification": `<strong>Eligibility:</strong><br>${bulletEligibility}<br><br><strong style="text-decoration: underline;">TERMS & CONDITIONS:</strong><br>${bulletTerms}`,
          eligibility: bulletEligibility,
          sender: sender,
          originalFileName: fileName
        }
      });
    }
  }
}

console.log(`\nâœ¨ Total: ${results.length} jobs created`);

return results;

